import React from "react";
import axios from "axios";

import { BrowserRouter as Router, Route } from "react-router-dom";

import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import {
  Button,
  Form,
  FormControl,
  Navbar,
  Nav,
  NavDropdown,
} from "react-bootstrap";
import { Link } from "react-router-dom";

import { LoginView } from "../login-view/login-view";
//import { RegistrationView } from "../registration-view/registration-view";
import { MovieView } from "../movie-view/movie-view";
import { MovieCard } from "../movie-card/movie-card";

export class MainView extends React.Component {
  constructor() {
    super();

    this.state = {
      movies: null,
      selectedMovie: null,
      user: null,
      // register: false,
    };
  }

  // One of the "hooks" available in a React Component
  componentDidMount() {
    axios
      .get("https://tranquil-river-08432.herokuapp.com/movies/")
      .then((response) => {
        // Assign the result to the state
        this.setState({
          movies: response.data,
        });
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  resetSelectedMovie() {
    this.setState({
      selectedMovie: null,
    });
  }
  onMovieClick(movie) {
    this.setState({
      selectedMovie: movie,
    });
  }

  onLoggedIn(user) {
    this.setState({
      user,
    });
  }
  render() {
    //   // If the state isn't initialized, this will throw on runtime
    //   // before the data is initially loaded
    const { movies, selectedMovie, user, register } = this.state;

    if (!user)
      return <LoginView onLoggedIn={(user) => this.onLoggedIn(user)} />;

    // if (register);
    // return (
    //   <RegistrationView
    //     onClick={() => this.alreadyMember()}
    //     onSignedIn={(user) => this.onSignedIn(user)}
    //   />
    // );

    if (!movies) return <div className="main-view" />;
    //     return (
    return (
      <React.Fragment>
        <Navbar bg="light" expand="lg">
          <Navbar.Brand>Cinema-App</Navbar.Brand>
          <Nav.Link href="login-view">Login</Nav.Link>
          <Nav.Link href="registration-view">Register</Nav.Link>
        </Navbar>
        {selectedMovie ? (
          <MovieView movie={selectedMovie} />
        ) : (
          <div className="main-view">
            {/* <Row className="mb-">
              <Col xs={12} sm={4}></Col>
              <Col xs={12} sm={4}></Col>
            </Row> */}
            {movies.map((movie) => (
              <MovieCard
                key={movie._id}
                movie={movie}
                onClick={(movie) => this.onMovieClick(movie)}
              />
            ))}
          </div>
        )}{" "}
      </React.Fragment>
    );
  }
}
